mapper.update:
UPDATE board
    SET

    SUBJECT = #{subject}

 , 

    CONTENT = #{content}

WHERE BOARD_ID = #{board_id}

mapper.delete:
delete
from
    board_reply
where
    reply_id = #{reply_id}
and reply_writer = #{reply_writer}

mapper.update:
update
    board_reply
set
    parent_id = #{parent_id},
    depth = #{depth},
    reply_content = #{reply_content}
where
    reply_id = #{reply_id}
and reply_writer = #{reply_writer}
